create table UM_SHOP (S_NAME varchar(32) not null, S_ADDR varchar(100), S_INTRO longtext, S_MEMO varchar(50), primary key (S_NAME));
create table UM_USER (U_ID varchar(32) not null, U_PASSWD varchar(15), U_ROLE varchar(15), U_EMAIL varchar(33), U_STATUS integer, U_LGD date, primary key (U_ID));
